const lastDailyReceived = await getValue(`${member.user.id}.lastDailyReceived`)
const timeout = 24 * 60 * 60 * 1000 // 24 hours
const dailyCoints = 150

if (!lastDailyReceived || Date.now() - lastDailyReceived > timeout) {
  await modifyUserWallet(member.user.id, dailyCoints)
  await reply({
    content: `**${member.user.username}**, вы получили ежедневное вознаграждение в размере **${dailyCoints}**`
  })
  setValue(`${member.user.id}.lastDailyReceived`, Date.now())
} else {
  await reply({
    content: `**${member.user.username}**, возвращайтесь <t:${Math.round((lastDailyReceived + timeout) / 1000)}:R>, чтобы получить награду еще раз`
  })
}
